DROP TABLE IF EXISTS `hx_govent_list`;
CREATE TABLE `hx_govent_list` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '索引id',
  `rank` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `shorttile` char(60) DEFAULT NULL COMMENT '简短标题',
  `intro` varchar(500) DEFAULT NULL COMMENT '简述',
  `content` text COMMENT '内容',
  `pic` varchar(100) DEFAULT NULL COMMENT '缩略图',
  `url` varchar(100) DEFAULT NULL COMMENT '跳转链接',
  `addtime` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '发布时间',
  `mid` int(11) DEFAULT '0' COMMENT '发布ID',
  `edittime` int(10) DEFAULT NULL COMMENT '修改时间',
  `editor` int(11) DEFAULT '0' COMMENT '修改ID',
  `ishot` tinyint(1) DEFAULT '0' COMMENT '0不热门,1热门',
  `hottime` int(11) DEFAULT NULL COMMENT '热门时间',
  `isrec` tinyint(1) DEFAULT '0' COMMENT '0不推荐1推荐',
  `rectime` int(11) DEFAULT NULL COMMENT '推荐时间',
  `clicks` int(11) DEFAULT '0' COMMENT '点击数',
  `ip` char(15) DEFAULT NULL COMMENT 'ip',
  `status` tinyint(1) unsigned DEFAULT '1' COMMENT '是否显示，0为否，1为是，默认为1',
  `seo_title` varchar(200) DEFAULT '' COMMENT 'SEO标题',
  `seo_keywords` varchar(200) DEFAULT '' COMMENT 'SEO关键词',
  `seo_description` text COMMENT 'SEO描述',
  `isdel` tinyint(1) DEFAULT '0' COMMENT '是否删除 默认0否 1是',
  `releaseid` int(10) DEFAULT '0' COMMENT '发布者id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=115 DEFAULT CHARSET=utf8 COMMENT='文章表';

